INSERT INTO survey_responses (citizen_id, tech_comfort_level, primary_barrier, interested_in_careers, desired_skill, biggest_concern, best_opportunity, gov_tech_suggestion, preferred_gov_service) VALUES
(1, 4, 'Cost of internet / devices', true, 'Data analysis', 'The high cost of internet makes it hard for regular people to participate in the digital economy.', 'Fintech could revolutionize banking access across the islands.', 'Create a single government portal for all services.', 'Online tax filing'),
(2, 3, 'Lack of training or skills', false, 'Digital marketing', 'Tourism industry needs better tech integration but workers lack training.', 'Digital tourism marketing could attract more visitors to Family Islands.', 'Better online booking systems for government-related tourism permits.', 'Business registration online'),
(3, 5, 'Cost of internet / devices', true, 'Coding / Software development', 'My generation wants tech careers but there are few opportunities in Nassau.', 'Young Bahamians could build apps and services for the Caribbean region.', 'Offer free Wi-Fi in all government buildings and public spaces.', 'Online education portal'),
(4, 3, 'Privacy / security concerns', false, 'Cybersecurity', 'Government systems are vulnerable to cyber attacks and data breaches.', 'Digitizing government services would reduce corruption and increase transparency.', 'Invest in cybersecurity training for all government employees.', 'Digital ID / passport renewal'),
(5, 2, 'Lack of training or skills', false, 'Basic computer literacy', 'Many teachers still do not know how to use technology effectively in classrooms.', 'Online learning could reach students on remote islands who lack resources.', 'Provide tablets and internet access to every school in the country.', 'Online education portal'),
(6, 5, 'Limited internet access in my area', true, 'AI / Machine learning', 'Brain drain - our best tech talent leaves for the US because there are no jobs here.', 'The Bahamas could become a Caribbean tech hub if we invest in infrastructure.', 'Build a government tech incubator and offer tax breaks for tech startups.', 'Business registration online'),
(7, 3, 'Cost of internet / devices', false, 'Digital marketing', 'Small businesses cannot afford the technology they need to compete.', 'E-commerce could open up Family Island businesses to the world.', 'Create small business tech grants and subsidize internet costs.', 'Online utility payments'),
(8, 2, 'Limited internet access in my area', false, 'Basic computer literacy', 'Andros has terrible internet coverage. We feel left behind.', 'Technology could help farmers track weather patterns and market prices.', 'Extend broadband to all inhabited islands, not just Nassau and Freeport.', 'Online utility payments'),
(9, 4, 'Privacy / security concerns', true, 'Data analysis', 'Patient data security is a major concern as we digitize health records.', 'Telemedicine could transform healthcare access for Family Island residents.', 'Implement a national electronic health records system with proper security.', 'E-health records'),
(10, 2, 'Lack of training or skills', false, 'Project management', 'Construction industry is behind on adopting modern technology.', 'Building Information Modeling could reduce costs and improve project delivery.', 'Require digital building permits and inspection reports.', 'Digital land registry'),
(11, 1, 'Don''t see the need', false, 'Basic computer literacy', 'Technology moves too fast for people my age. We get left behind.', 'Video calls let me stay connected with grandchildren in Nassau.', 'Offer free computer classes for seniors at community centers.', 'Digital ID / passport renewal'),
(12, 4, 'Cost of internet / devices', true, 'Graphic design', 'Starting a business in the Bahamas requires too much paperwork and in-person visits.', 'Digital tools let small businesses punch above their weight in global markets.', 'All business registration and licensing should be 100% online.', 'Business registration online'),
(13, 3, 'Limited internet access in my area', false, 'Digital marketing', 'Cat Island tourists want to book online but our internet is unreliable.', 'Family Islands could develop eco-tourism through better online presence.', 'Install community Wi-Fi hotspots in all settlement centers.', 'Government job portal'),
(14, 5, 'Cost of internet / devices', true, 'Coding / Software development', 'We need more hackathons and coding competitions to build a tech culture.', 'Bahamian developers could create solutions for Caribbean-specific problems.', 'Partner with tech companies to create internship programs for young Bahamians.', 'Online education portal'),
(15, 2, 'Lack of local tech support', false, 'Cybersecurity', 'Government websites go down frequently and are hard to navigate.', 'E-government could save people from traveling to Nassau for every document.', 'Hire competent IT professionals and pay them competitive salaries.', 'Online court services'),
(16, 4, 'Cost of internet / devices', true, 'AI / Machine learning', 'Banking fees are too high and technology could help bring them down.', 'Digital banking and crypto could provide financial inclusion for the unbanked.', 'Support the Sand Dollar digital currency and reduce transaction fees.', 'Online tax filing'),
(17, 3, 'Lack of training or skills', false, 'Project management', 'Teachers need ongoing professional development in technology.', 'Blended learning models could improve education outcomes across the islands.', 'Create a national teacher training program for educational technology.', 'Online education portal'),
(18, 4, 'Cost of internet / devices', true, 'Digital marketing', 'Young people in Bimini have to leave for better opportunities.', 'Marine technology and tourism tech could create jobs on smaller islands.', 'Create a youth tech apprenticeship program.', 'Government job portal'),
(19, 3, 'Privacy / security concerns', false, 'Cybersecurity', 'Hospital record systems are outdated and error-prone.', 'AI diagnostics could help our understaffed clinics serve patients better.', 'Implement telemedicine for Family Island clinics.', 'E-health records'),
(20, 2, 'Limited internet access in my area', false, 'Basic computer literacy', 'Inagua has the worst connectivity in the country. We need help.', 'Technology could help environmental monitoring and conservation efforts.', 'The government must treat internet as a basic utility.', 'Emergency alert system'),
(21, 4, 'Cost of internet / devices', false, 'Digital marketing', 'Cost of living in Freeport makes it hard to invest in technology.', 'Grand Bahama could attract remote workers with better infrastructure.', 'Reduce import duties on computers and tech equipment.', 'Online utility payments'),
(22, 2, 'Lack of training or skills', false, 'Project management', 'Many tradespeople could benefit from technology but nobody teaches us.', 'Construction tech could create safer building practices in hurricane zones.', 'Offer trade-specific technology courses at BTVI.', 'Digital land registry'),
(23, 3, 'Limited internet access in my area', false, 'Data analysis', 'Farming in Acklins is still done the old way. Technology could help.', 'Agricultural technology could help food security and reduce imports.', 'Create a national agricultural technology program.', 'Business registration online'),
(24, 4, 'Lack of local tech support', true, 'Graphic design', 'When something breaks there is nobody qualified to fix it on the outer islands.', 'Abaco could attract tech workers looking for island lifestyle.', 'Train local technicians on every major island.', 'Business registration online'),
(25, 5, 'Cost of internet / devices', true, 'Coding / Software development', 'I want to study computer science but COB barely offers courses.', 'Bahamian youth could lead tech innovation in the Caribbean.', 'Offer coding boot camps and scholarships for tech education.', 'Online education portal'),
(26, 3, 'Limited internet access in my area', false, 'Digital marketing', 'Eleuthera tourism could boom with better online marketing tools.', 'Sustainable tourism tech could be a model for the Caribbean.', 'Build out fiber optic connections to all major Family Islands.', 'Online utility payments'),
(27, 1, 'Lack of training or skills', false, 'Basic computer literacy', 'At my age, even using a smartphone is challenging.', 'Keeping in touch with family through technology is wonderful.', 'Have patient young people teach seniors at churches and community centers.', 'Digital ID / passport renewal'),
(28, 4, 'Cost of internet / devices', true, 'Data analysis', 'Financial services sector needs to modernize or lose business to other jurisdictions.', 'The Bahamas could lead Caribbean fintech innovation.', 'Create regulatory sandbox for fintech startups.', 'Online tax filing'),
(29, 3, 'Lack of training or skills', false, 'Project management', 'Berry Islands schools need better technology resources.', 'Distance learning could equalize education between Nassau and Family Islands.', 'Every school should have reliable internet and modern computers.', 'Online education portal'),
(30, 5, 'Cost of internet / devices', true, 'AI / Machine learning', 'Internet costs in the Bahamas are among the highest in the Caribbean.', 'The Bahamas could become a Caribbean AI research and development center.', 'Regulate internet providers to bring down costs and improve service.', 'Business registration online');
